let btnToggle = document.querySelector('.btn__toggle');
let navActive = document.querySelector('.main-nav');


btnToggle.addEventListener('click', function () {
    if (btnToggle.classList.contains('btn__toggle-active')) {
        btnToggle.classList.remove('btn__toggle-active')
        navActive.classList.remove('active')
    } else {
        btnToggle.classList.add('btn__toggle-active')
        navActive.classList.add('active')
    }
})

